# -*- coding: utf-8 -*-
from abc import abstractmethod, ABCMeta


class AOVsABC:
    __metaclass__ = ABCMeta

    @abstractmethod
    def create_aov(self, name, aov_type = None, aov_filter = None):
        pass

    @abstractmethod
    def create_crypto_matte(self):
        pass

    @abstractmethod
    def create_ao(self):
        pass

    @abstractmethod
    def delete_aov(self, name):
        pass

    @abstractmethod
    def all_light_group(self, name):
        pass
