# -*- coding: utf-8 -*-


def create_aov(factory, name, aov_type=None, aov_filter=None):
    """
        创建Maya的aov

    :param factory:  aov工厂(Arnold AOV,Redshift AOV)
    :param name:  aov名字
    :param aov_type: aov类型
    :param aov_filter: aov过滤器:
    :return:
    """
    aov = factory.createAOV()
    aov.create_aov(name, aov_type, aov_filter)
    return aov


def create_crypto_matte_aov(factory):
    aov = factory.createAOV()
    aov.create_crypto_matte()
    return aov


def create_ao(factory):
    aov = factory.createAOV()
    aov.create_ao()
    return aov
