# -*- coding: utf-8 -*-

class CGTAssetTask(object):
    def __init__(self, project_db, task_id, login_strategy):
        self.project_db = project_db
        self.task_id = task_id
        self.t_tw = login_strategy.login()

    def get_path_with_sign(self, sign, make_dir_strategy):
        path = self.t_tw.task.get_sign_filebox(self.project_db, self.module, self.task_id, sign)

    @property
    def module(self):
        return "asset"
