# -*- coding: utf-8 -*-
from m_cgt_py2.src.task import CGTTask


class CGTAssetTask(CGTTask):
    def __init__(self, project_db, task_id, login_strategy):
        super(CGTAssetTask, self).__init__(project_db, task_id, login_strategy, "asset")

    @property
    def asset_name(self):
        return self.t_tw.task.get(self.project_db, self.module, [self.task_id], ["asset.entity"])[0]["asset.entity"]

    @property
    def asset_type(self):
        return self.t_tw.task.get(self.project_db, self.module, [self.task_id], ["asset_type.entity"])[0][
            "asset_type.entity"]
