# -*- coding: utf-8 -*-
from m_maya_py2.src.node import MayaNodePy2


class MayaAttributePy2(MayaNodePy2):
    def __init__(self, maya_node):
        super(MayaAttributePy2, self).__init__(maya_node)
        self._maya_node = maya_node

    def get_attribute(self, name):
        """
            添加一个属性

        Args:
            name: 属性名字
        Raises:
             AttributeError: 当属性不存在时候抛出异常

        Returns:
            属性值

        """
        self._check_node_exist()
        try:
            return self.cmds.getAttr("{}.{}".format(self.node_path, name))
        except:
            raise AttributeError("{}.{} is not exist".format(self.node_path, name))

    @property
    def node(self):
        return self._maya_node

    @property
    def node_path(self):
        return self._maya_node.node_path
