# -*- coding: utf-8 -*-


class MayaBasePy2:
    def __init__(self):
        pass

    def load_plugin(self, plugin_name):
        """
            加载插件

        :param plugin_name: 插件名字
        :return:
        """
        if not self.cmds.pluginInfo(plugin_name, q=1, l=1):
            self.cmds.loadPlugin(plugin_name)

    @property
    def version(self):
        return int(self.cmds.about(version=True))

    @staticmethod
    def cmds():
        import maya.cmds as cmds
        return cmds

    @staticmethod
    def mel():
        import maya.mel as mel
        return mel
