# -*- coding: utf-8 -*-
from m_maya_py2.src.node import MayaNode
from m_maya_py2.src.plugins.attribute import MayaAttribute


class MayaGroup(MayaNode):
    def __init__(self, node):
        # type: (str) -> None
        super(MayaGroup, self).__init__(node)

    def create_new(self, name):
        # type: (str) -> 'MayaAttribute'
        group_name = self.cmds.group(n=name, empty=True)
        return MayaAttribute(group_name)

    def add_child(self, name, parent):
        # type: (str,'MayaNode') -> 'MayaAttribute'
        group = self.cmds.group(n=name, empty=True)
        self.cmds.parent(group, parent)
        return MayaAttribute(group)

    def add_child_current(self, name):
        # type: (str) -> 'MayaAttribute'
        group = self.cmds.group(n=name, empty=True)
        self.cmds.parent(group, self.node_path)
        return MayaAttribute(group)

    def add_children(self, names, parent):
        for n in names:
            yield self.add_child(n, parent)
