# -*- coding: utf-8 -*-
from m_maya_py2.src.plugins.base import MayaPluginBase


class MayaArnoldBase(MayaPluginBase):
    def __init__(self):
        super(MayaArnoldBase, self).__init__()
        self.load_plugin(self.plugin_name)

    @property
    def arnold_menu(self):
        import mtoa.ui.arnoldmenu as arnoldmenu
        return arnoldmenu

    @property
    def plugin_name(self):
        return "mtoa.mll"
