# -*- coding: utf-8 -*-
from m_maya_py2.src.node import MayaNode


class MayaAttribute:
    def __init__(self, maya_node):
        # type: (MayaNode) -> None
        self._maya_node = maya_node

    @property
    def node(self):
        return self._maya_node

    @property
    def node_path(self):
        return self._maya_node.node_path
