# -*- coding: utf-8 -*-
from m_maya_py2.src.base import MayaBasePy2


class MayaPluginBase:
    def __init__(self):
        self._maya_base = MayaBasePy2()

    def load_plugin(self, name):
        # type: (str) -> None
        if not self.cmds.pluginInfo(name, q=True, l=True):
            self.cmds.loadPlugin(name)

    def unload_plugin(self, name, force=True):
        # type: (str,bool) -> None
        if self.cmds.pluginInfo(name, q=True, l=True):
            self.cmds.unloadPlugin(name, f=force)

    @property
    def cmds(self):
        return self._maya_base.cmds()
