# -*- coding: utf-8 -*-
import os

import yaml


def get_shader_attribute_data():
    """
        获取shader 属性
    Returns:

    """
    config_path = os.path.join(os.path.dirname(__file__), "config", "shader_attribute.yaml")
    with open(config_path, "r") as f:
        data = yaml.safe_load(f)
    return data
